mapper.select:
SELECT HASH_SEQ, VALUE
	FROM HASH
	
		
			
				VALUE = #{value}

SELECT VALUE
	FROM LINKHASH JOIN HASH USING(HASH_SEQ)
	WHERE CL_SEQ = #{cl_seq}

SELECT CL_SEQ, CL_TITLE, PRICE FROM (SELECT CL_SEQ, CL_TITLE, PRICE, ROW_NUMBER() OVER(ORDER BY PRICE) AS RN FROM CLASS c WHERE EXISTS (SELECT 1 FROM CLASS JOIN LINKHASH l USING(CL_SEQ) JOIN HASH USING(HASH_SEQ) WHERE VALUE IN
#{hash}
) ORDER BY RN) WHERE RN < 5

SELECT CL_SEQ
	FROM CLASS
	WHERE CL_SEQ = #{cl_seq} AND TEACHER = #{id}

SELECT CL_SEQ, CL_TITLE, CL_CONTENT, TEACHER, PRICE, STARTDAY, ENDDAY, c.ADDRESS
	FROM CLASS c JOIN STUDENT s USING(CL_SEQ) 
	WHERE STUDENT = #{id}

SELECT CL_SEQ, CL_TITLE, CL_CONTENT, TEACHER, PRICE, STARTDAY, ENDDAY, c.ADDRESS
	FROM CLASS c 
	WHERE TEACHER=#{id}
